import { createApp } from 'vue';
import App from './App.vue';
import { createRouter, createWebHistory } from 'vue-router';
import axios from 'axios';





import LoginPage from './components/LoginPage.vue';
import SignUpPage from './components/SignUpPage.vue';
import AdminPage from './components/MainPage.vue';











// Set base URL for Axios
axios.defaults.baseURL = 'http://localhost:9090';

// Define routes
const routes = [
    {
        path: '/',
        component: LoginPage,
        children: [
            { path: '/login', name: 'LoginPage', component: LoginPage },


            { path: '/signup', name: 'SignUpPage', component: SignUpPage },
            { path: '/admin', name: 'MainPage', component: AdminPage },








        ]
    }
];

// Create router instance
const router = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes
});

// Create Vuetify instance
const vuetify = createVuetify({
    components,
    directives,
});

// Create Vue app instance
const app = createApp(App);
app.use(router);
app.use(vuetify);
app.config.globalProperties.$axios = axios;
app.mount('#app');

// Add navigation guard
router.beforeEach((to, from, next) => {
    const isAuthenticated = !!localStorage.getItem('authToken'); // Check if user is authenticated

    if (to.name === 'JobApplication' && !isAuthenticated) {
        next({ name: 'LoginPage' }); // Redirect to login if not authenticated
    } else {
        next(); // Proceed if authenticated
    }
});
