import Vue from 'vue';
import App from './App.vue';
import router from './router'; // Import router

Vue.config.productionTip = false;

new Vue({
    render: h => h(App),
    router, // Register the router
    beforeCreate() {
        // If a user is already logged in (i.e., token exists in localStorage), redirect to the main page
        const token = localStorage.getItem('token');
        if (token && this.$route.path === '/') {
            this.$router.push('/main'); // Redirect to main page
        }
    },
}).$mount('#app');
